mapper.update:
update food
set name = #{name,jdbcType=VARCHAR},
  type = #{type,jdbcType=VARCHAR},
  amount = #{amount,jdbcType=INTEGER},
  price = #{price,jdbcType=DECIMAL}
where name = #{name,jdbcType=VARCHAR}

mapper.select:
select name, type, amount, price
from food
where name = #{name,jdbcType=VARCHAR}
